/**
* setLanguage 
* use $.lang[currentLanguage][languageNumber]
*/
function setLanguage(currentLanguage) {
    $('[data-lang]').each(function() {
        var $this = $(this); 
        $this.html($.lang[currentLanguage][$this.data('lang')]); 
    }); 
}

function getLanguage(c) {
    var lang = (typeof c == 'undefined') ? $.cookie('ci_userlang') : '';
    if(!lang) lang = navigator.language || navigator.userLanguage;
    switch(lang) {
        case "ko":
        case "ko-KR":
        case "korean":
            lang = "ko";
            break;
        case "en":
        case "en-US":
        case "english":
            lang = "en";
            break;
        case "ja":
            lang = "ja";
            break;
        default:
            lang = "ko";
            break;
    }
    return lang;    
}

var getKeyByValue = function( array, value ) {
    for( var prop in array ) {
        if( array.hasOwnProperty( prop ) ) {
            //console.log(array[ prop ]);
            if( array[ prop ].toUpperCase() == value.toUpperCase() )
                return prop;
        }
    }
    return "";
}    
